import Head from "next/head";
import Layout, { siteTitle } from "../components/Layout";
import { getCocktailData } from "../lib/API";
import CocktailsList from "../components/CocktailsList";
import mockData from "../mockData.json";

export async function getStaticProps() {
  const allCocktailsData = await getCocktailData();
  return {
    props: {
      allCocktailsData,
    },
  };
}

export default function Home({ allCocktailsData }) {
  return (
    <Layout home>
      <Head>â€¦</Head>

      <div className="hero col s12"></div>
      <div className="card-panel search">
        <div className="row">
          <div className="input-field col s6">
            <form>
              <input value="" id="search" type="text" className="validate" />
              <label className="active" for="search">
                Search for a cocktail e.g. Negroni
              </label>
            </form>
          </div>
          <br />
          <span>
            <button
              className="btn waves-effect waves-light"
              type="submit"
              name="action"
            >
              Search
            </button>
          </span>
        </div>
      </div>
      <style global jsx>{`
        .hero {
          background: url("/images/hero.jpg") no-repeat 0% 0%;
          background-size: cover;
          height: 100vh;
        }
        .search {
          position: absolute;
          top: 50%;
          left: 50%;
        }
      `}</style>
    </Layout>
  );
}
