import firebase from "firebase/app";
import "firebase/auth";
import initFirebase from "../utils/initFirebase";

initFirebase();

export const addFavourite = (fav) => async (dispatch) => {
  console.log("addFavourite");
  firebase
    .firestore()
    .collection("favs")
    .add({
      fav: post,
      idDrink: "11728",
      strDrink: "Martini",
      strCategory: "Cocktail",
      strIBA: "Unforgettables",
      strAlcoholic: true,
      strGlass: "Cocktail glass",
      ingredients: {
        "1": "Gin",
        "2": "Dry vermouth",
        "3": "Olive",
      },
      timestamp: db.firestore.FieldValue.serverTimestamp(),
    })
    .then(() => {
      console.log("added fav!!");
    })
    .catch((error) => {
      console.error("Error adding document: ", error);
    });
};

//This sets up the listener to fetch posts.
//This pulls back an initial 50 posts but also sets
//a listener so as new posts fill in their are added to the top.
export const fetchFavs = () => async (dispatch) => {
  const db = await loadDB();

  db.firestore()
    .collection("favs")
    .orderBy("timestamp", "desc")
    .limit(50)
    .onSnapshot((snapshot) => {
      let newState = {
        favs: [],
      };

      snapshot.forEach(function (doc) {
        newState.posts.push({
          id: doc.id,
          post: doc.data().post,
        });
      });

      dispatch({
        type: FETCH_POSTS,
        payload: newState,
      });
    });
};
