import React, { useState } from "react";
import Card from "../components/Card";

export default function CocktailsList({ drinks }) {
  const [selectedDrink, setSelectedDrink] = useState(drinks.drinks[0]);

  function setDetailsVisible(id) {
    const drink = drinks.drinks.find((drink) => drink.idDrink === id);
    setSelectedDrink(drink);
    console.log(drink);
  }

  return (
    <>
      <div class="row">
        <div class="col s12 m8 l9">
          {drinks.drinks &&
            drinks.drinks.map(
              ({
                idDrink,
                strDrinkThumb,
                strDrink,
                strIBA,
                strAlcoholic,
                strIngredient1,
                strIngredient2,
                strIngredient3,
                strIngredient4,
                strIngredient5,
                strIngredient6,
                strIngredient7,
              }) => (
                <Card
                  id={idDrink}
                  strDrinkThumb={strDrinkThumb}
                  strDrink={strDrink}
                  strIBA={strIBA}
                  strAlcoholic={strAlcoholic}
                  selectDrink={(e) => setDetailsVisible(idDrink)}
                />
              )
            )}
        </div>
        <div class="col s12 m4 l3 grey lighten-2 full-height">
          <p>{selectedDrink.strDrink}</p>
          <p>{selectedDrink.strAlcoholic}</p>
          <ul>
            <li>
              {" "}
              {selectedDrink.strIngredient1 && selectedDrink.strIngredient1}
            </li>
            <li>
              {" "}
              {selectedDrink.strIngredient2 && selectedDrink.strIngredient2}
            </li>
            <li>
              {" "}
              {selectedDrink.strIngredient3 && selectedDrink.strIngredient3}
            </li>

            <li>
              {" "}
              {selectedDrink.strIngredient4 && selectedDrink.strIngredient4}
            </li>
            <li>
              {" "}
              {selectedDrink.strIngredient5 && selectedDrink.strIngredient5}
            </li>
          </ul>
          <p>Serve in a {selectedDrink.strGlass}</p>
          <p>{selectedDrink.strInstructions}</p>
        </div>
      </div>
      <style global jsx>{`
        .full-height {
          min-height: 100vh;
        }
      `}</style>
    </>
  );
}
