import firebase from "firebase/app";
import "firebase/auth";
import initFirebase from "../utils/auth/initFirebase";

export const addFavourite = (fav) => {
  initFirebase();

  console.log(firebase.auth().currentUser);
  if (firebase.auth().currentUser) {
    const userId = firebase.auth().currentUser.uid;

    firebase
      .collection("cities")
      .doc(req.query.name)
      .get()
      .then((doc) => {
        res.json(doc.data());
      })
      .catch((error) => {
        res.json({ error });
      });
    firebase
      .firestore()
      .collection("favs")
      .add({
        user: userId,
        fav: fav,
        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      })
      .then(() => {
        console.log("Successfully added favourite!");
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  } else {
    console.log("there is no current user");
  }
};

//This sets up the listener to fetch data.
//This pulls back an initial 50 posts but also sets
//a listener so as new entries fill in they are added to the top.
export const getFavourites = () => {
  initFirebase();
  if (firebase.auth().currentUser) {
    const userId = firebase.auth().currentUser.uid;
    console.log(userId);

    firebase
      .firestore()
      .collection("favs")
      .where("user", "==", userId)
      .get()
      .then((doc) => {
        if (!doc.exists) {
          console.log("No such document!");
        } else {
          console.log("Document data:", doc.data());
        }
      })
      .catch((err) => {
        console.log("Error getting document", err);
      });
  }
};
