import firebase from "firebase/app";
import "firebase/auth";
import initFirebase from "../utils/auth/initFirebase";

const getCurrentUser = () => {
  return;
};

export const addFavourite = (fav) => {
  console.log(firebase.auth().currentUser);
  if (firebase.auth().currentUser) {
    initFirebase();
    firebase
      .firestore()
      .collection("favs")
      .add({
        fav: fav,
        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      })
      .then(() => {
        console.log("Successfully added favourite!");
      })
      .catch((error) => {
        console.error("Error adding document: ", error);
      });
  } else {
    console.log("there is no current user");
  }
};

//This sets up the listener to fetch posts.
//This pulls back an initial 50 posts but also sets
//a listener so as new posts fill in their are added to the top.
export const fetchFavs = () => async (dispatch) => {
  firebase
    // .firestore()
    .collection("favs")
    .orderBy("timestamp", "desc")
    .limit(50)
    .onSnapshot((snapshot) => {
      let newState = {
        favs: [],
      };

      snapshot.forEach(function (doc) {
        newState.posts.push({
          id: doc.id,
          post: doc.data().post,
        });
      });

      dispatch({
        type: FETCH_POSTS,
        payload: newState,
      });
    });
};
