import { useState } from "react";
import Head from "next/head";
import Router, { useRouter } from "next/router";
import Layout, { siteTitle } from "../components/Layout";
import CocktailsList from "../components/CocktailsList";
import mockData from "../mockData.json";

export default function Home({ allCocktailsData }) {
  const [searchValue, setSearchValue] = useState("");
  const router = useRouter();

  return (
    <Layout home>
      <Head>â€¦</Head>

      <div className="hero col s12"></div>
      <div className="card-panel search">
        <div className="row">
          <div className="input-field col s6">
            <input
              id="search"
              type="text"
              className="validate"
              onChange={(e) => setSearchValue(e.target.value)}
            />
            <label className="active" htmlFor="search">
              Search for a cocktail e.g. Negroni
            </label>
          </div>
          <br />
          <span>
            <button
              className="btn waves-effect waves-light"
              type="submit"
              name="action"
              onClick={() =>
                Router.push({
                  pathname: "/results",
                  query: { keyword: searchValue },
                })
              }
            >
              Search
            </button>
          </span>
        </div>
      </div>
      <style global jsx>{`
        .hero {
          background: url("/images/hero.jpg") no-repeat 0% 0%;
          background-size: cover;
          height: 100vh;
        }
        .search {
          position: absolute;
          top: 50%;
          left: 50%;
        }
      `}</style>
    </Layout>
  );
}
